    def inv_num_increment(inum):
      result = ""
      numberStr = ""
      invnum = inum
      ino = str(invnum).split("/")[0]
      i = len(ino) - 1
      while i > 0:
        c = ino[i]
        if not c.isdigit():
          break
        numberStr = c + numberStr
        i -= 1
      number = int(numberStr)
      number += 1
      result += ino[0 : i + 1]
      result += "0000" if number < 1000 else ""
      result += str(number)
      y = datetime.today().date()
      year = "/" + "" + str(y.year)
      result += year
      return result
    
