      #################### size convertion of files############################
      def convertion_1(B):
        BYTE = float(B)
        KB = float(1024)
        MB = float(KB**2)

        if BYTE < KB:
          return '{0} {1}'.format(BYTE,'Bytes' if 0 == B > 1 else 'Byte')
        elif KB <= BYTE < MB:
          return '{0:.2f} KB'.format(BYTE / KB)
        elif MB <= BYTE:
          return '{0:.2f} MB'.format(BYTE / MB)
